import { useRef } from "react"
const Input = ({color, setColor, setHexValue}) => {
    const newRef = useRef();
  return (
        <form onSubmit={(e)=>(e.preventDefault())}>
            <label htmlFor="color">Add Color Name:</label>
            <input
                id = "color"   
                type="text"
                placeholder="Add color name" 
                autoFocus
                required
                ref = {newRef}
                value = {color}
                onChange={(e)=>{
                    setColor(e.target.value)
                    setHexValue(e.target.value.toHex())
                }}
            />
            <button hidden onClick={()=>newRef.current.focus()}>Submit</button>
        </form>
  )
}

export default Input
