import { useState, useEffect } from 'react';
import Header from './Header';
import Content from './Content';

function App() {
  const API_URL = 'https://jsonplaceholder.typicode.com/';
  const [items, setItems] = useState([]);
  const[currPage, setCurrPage] = useState('Users');


  useEffect(()=>{
    setIsLoading(true);
    const fetchItems = async ()=>{
      try {
        const currReqUrl = `${API_URL}${currPage.toLocaleLowerCase()}`;
        const response = await fetch(currReqUrl);
        const listItems = await response.json();
        setItems(listItems);

        setFetchError(null);
      } catch (err){
        console.log(err.message);
      }
    }
    fetchItems();
  },[currPage])


  return (

    <div className="App">
        <Header 
          currPage = {currPage}
          setCurrPage = {setCurrPage}
        />
        <main>
            {!isLoading && <p>Loading items....</p>}
            {fetchError && <p style={{color:'red'}}>{`Error: ${fetchError}`}POTCHIE</p>}
            {!fetchError && !isLoading && <Content items={items} /> }
        </main>
    </div>
  );
}

export default App;
