import { useState, useEffect } from 'react';
import Container from './Container';
import Content from './Content';

function App() {
  const API_URL = 'https://jsonplaceholder.typicode.com';
  const [items, setItems] = useState([]);
  const [fetchError, setFetchError] = useState(null);

  const [isLoading, setIsLoading] = useState(true);

  const[currPage, setCurrPage] = useState('Users');

  const fetchItems = async ()=>{
    const currReqUrl = `${API_URL}/${currPage.toLocaleLowerCase()}`;
    try {
      const response = await fetch(currReqUrl);
      if(!response.ok) throw ('Did not received expected data');
      const listItems = await response.json();
      setItems(listItems);
      setFetchError(null);
    } catch (err){
      setFetchError(err.message);

    }finally{
      setIsLoading(false);
      console.log({currReqUrl});
    }
  }

  useEffect(()=>{
    (async()=> await fetchItems())();
  },[])


  const handleOnClick=(page)=>{
    setCurrPage(page);
    (async()=> await fetchItems())();
  }


  return (

    <div className="App">
        <main>
            <Container 
              currPage = {currPage}
              handleOnClick = {handleOnClick}
            />
            { items && !isLoading && <Content items={items} /> }
        </main>
    </div>
  );
}

export default App;
