const Buttons = ({currPage,handleOnClick}) => {
  return (
    <form onSubmit={(e)=>(e.preventDefault())}>
        <button
            className={currPage === "Users" ? "selected" : "" }
            onClick={(e)=>handleOnClick((e.target.innerText).toLocaleLowerCase())}
        >
            Users
        </button>
        <button 
            className={currPage === "Posts" ? "selected" : "" }
            onClick={(e)=>handleOnClick((e.target.innerText).toLocaleLowerCase())}
        >
            Posts
        </button>
        <button
            className={currPage === "Comments" ? "selected" : "" }
            onClick={(e)=>handleOnClick((e.target.innerText).toLocaleLowerCase())}
        >
            Comments
        </button>
    </form>
  )
}

export default Buttons
