import { useState, useEffect } from 'react';
import Header from './Header';
import Content from './Content';

function App() {
  const API_URL = 'https://jsonplaceholder.typicode.coms';
  const [items, setItems] = useState([]);
  const [fetchError, setFetchError] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const[currPage, setCurrPage] = useState('Users');


  useEffect(()=>{
    const fetchItems = async ()=>{
      const currReqUrl = `${API_URL}/${currPage.toLocaleLowerCase()}`;
      try {
        const response = await fetch(currReqUrl);
        console.log(response);
        if(!response.ok) throw ('Did not received expected data');
        const listItems = await response.json();
        setItems(listItems);
        setFetchError(null);
      } catch (err){
        setFetchError(err.message);
        console.log(`{fetchError} from catch`);
      }finally{
        console.log(fetchError);
        console.log(`{fetchError} from finally`);
      }
    }
    (async()=> await fetchItems())();
  },[currPage])


  return (

    <div className="App">
        <Header 
          currPage = {currPage}
          setCurrPage = {setCurrPage}
        />
        <main>
            {isLoading && <p>Loading items....</p>}
            {fetchError && <p style={{color:'red'}}>{`Error: ${fetchError}`}POTCHIE</p>}


            {!fetchError && !isLoading && <Content items={items} /> }
        </main>
    </div>
  );
}

export default App;
