import {useState,useEffect} from 'react';
import Container from './Container';
import Content from './Content';

function App() {
  const API_URL = 'https://jsonplaceholder.typicode.com/';
  const [items, setItems] = useState([]);
  const [fetchError, setFetchError] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  const[currPage, setCurrPage] = useState('Users');

  useEffect(()=>{
    const currReqUrl = `${API_URL}/${currPage.toLocaleLowerCase()}`;
    const fetchItems = async ()=>{
      try {
        const response = await fetch(currReqUrl);
        if(!response.ok) throw ('Did not received expected data');
        const listItems = await response.json();
        setItems(listItems);
        setFetchError(null);
      } catch (err){
        setFetchError(err.message);
      }finally{
        setIsLoading(false);
      }
      (async()=> await fetchItems())();
    }
  },[])

  return (

    <div className="App">
        <main>
            <Container 
              currPage = {currPage}
              setCurrPage = {setCurrPage}
            />
            <Content listItems={listItems} />
        </main>
    </div>
  );
}

export default App;
